import axios from "axios";
import { useEffect,useState } from "react";
import { useParams } from "react-router-dom"


export const UserDetails=()=>{
const { id }=useParams();

const [user,setUser]=useState({})


// useEffect(()=>{
//     axios.get(`https://reqres.in/api/users/${id}`).then(({data})=>{
//      setUser(data.data);
//     });
// },[])
useEffect(() => {
    fetch(`http://localhost:8080/employee/${id}`)
         .then(response => response.json())
         .then(data =>
              setUser(data))

}, []);

    return (
    <div>
       <img src={user.image} alt="" />
        <div>First Name:{user.title}</div>
        
    </div>
    )
};

export const EmployeeDetails = () => {
    return (
      <div className="user_details">
        <img className="user_image" />
        <h4 className="user_name"></h4>
        <span className="user_salary">$</span>
        <span className="tasks">
          <li className="task"></li>
        </span>
        Status: <b className="status"></b>
        Title: <b className="title"></b>
        {/* Show this button only if user is not already terminated (users status is working) */}
        <button className="fire">Fire Employee</button>
        {/* Show this button only if user is not already team lead or terminated */}
        <button className="promote">promote</button>
      </div>
    );
  };
